import React from 'react';
import styles from './Counter.module.css';

const words = ["I like to code because coding is fun. Ok, not really, but it pays the bills."];

const Counter = () => {
  var rows = [];
  for (var i = 0; i < words.length; i++) {
    rows.push("<div>" + words[i] + "</div>");
  }
  return rows;
}

Counter.propTypes = {};

Counter.defaultProps = {};

export default Counter;
